mapper.insert:
INSERT INTO MEMBER
VALUES (null
	 , #{name}
	 , #{id}
     , #{passwd}
     , #{post_code}
     , #{address1}
     , #{address2}
     , #{email}
     , #{job}
     , #{gender}
     , #{hobby}
     , #{motivation}
     , NOW()
	 , null
     , 1
     , 'N')

mapper.select:
SELECT COUNT(*) AS CNT
  FROM MEMBER
 WHERE ID = #{id}

SELECT *
  FROM MEMBER
 WHERE ID = #{id}
   AND PASSWD = #{passwd}

mapper.update:
	UPDATE MEMBER
       SET NAME = #{name}

		 , PASSWD = #{passwd} -- 비밀번호 변경시에만		

         , POST_CODE = #{post_code}
         , ADDRESS1 = #{address1}
         , ADDRESS2 = #{address2}
         , EMAIL = CONCAT(#{email1}, '@', #{email2})
         , JOB = #{job}
	     , GENDER = #{gender}
         , HOBBY = #{hobby}
         , MOTIVATION = #{motivation}
   	 WHERE ID = #{id}
